--
-- Cấu trúc bảng cho bảng `products`
--

CREATE TABLE `products` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `feature_image` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `price` int(11) NOT NULL,
  `description` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `status` tinyint(4) NOT NULL DEFAULT 1,
  `featured` tinyint(4) NOT NULL DEFAULT 0,
  `user_id` bigint(20) UNSIGNED NOT NULL,
  `category_product_id` bigint(20) UNSIGNED NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `products`
--

INSERT INTO `products` (`id`, `name`, `slug`, `feature_image`, `price`, `description`, `status`, `featured`, `user_id`, `category_product_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 'Samsung Galaxy A31', 'samsung-galaxy-a31', 'public/uploads/product/1\\sam-a31-den-1.jpg', 12000000, '<p><strong>Sở hữu&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung-galaxy-a31\" target=\"_blank\" rel=\"noopener\">Samsung Galaxy A31</a>, bạn sẽ c&oacute; được trải nghiệm h&igrave;nh ảnh kh&ocirc;ng thua k&eacute;m&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">smartphone</a>&nbsp;cao cấp n&agrave;o với m&agrave;n h&igrave;nh rộng 6.4 inch rực rỡ sắc n&eacute;t. Ngo&agrave;i ra, khả năng chụp ảnh chuy&ecirc;n nghiệp nhờ cụm bốn camera tr&ecirc;n mặt lưng cũng l&agrave; điểm cộng kh&ocirc;ng thể bỏ qua khi nhắc tới Galaxy A31.</strong></p>\r\n<p><img id=\"M&ocirc; tả sản phẩm Samsung Galaxy A31 11\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-1.jpg\" alt=\"M&ocirc; tả sản phẩm Samsung Galaxy A31 1\" /></p>\r\n<h3><strong>G&oacute;i gọn trải nghiệm giải tr&iacute; v&agrave;o m&agrave;n h&igrave;nh 6.4 inch</strong></h3>\r\n<p>Lần đầu cầm Galaxy A31 tr&ecirc;n tay, bạn sẽ lập tức bị thu h&uacute;t bởi m&agrave;n h&igrave;nh Infinity-U 6.4 inch của chiếc điện thoại n&agrave;y. C&aacute;ch thiết kế đưa camera selfie v&agrave;o kh&ocirc;ng gian hiển thị gi&uacute;p&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung\">Samsung</a>&nbsp;r&uacute;t gọn viền bao quanh mặt trước v&agrave; tạo hiệu ứng m&agrave;n h&igrave;nh tr&agrave;n cạnh cuốn h&uacute;t. Tấm nền<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/man-hinh-super-amoled-la-gi-vi-sao-day-la-con-bai-chien-luoc-cua-samsung-57061\">&nbsp;Super AMOLED</a>&nbsp;độ ph&acirc;n giải Full HD+ của m&agrave;n h&igrave;nh Galaxy A31 sẽ đưa bạn v&agrave;o thế giới giải tr&iacute; hấp dẫn khi thưởng thức c&aacute;c bộ phim bom tấn, chơi những tựa game y&ecirc;u th&iacute;ch v&agrave; theo d&otilde;i sự kiện livestream.</p>\r\n<p><img id=\"G&oacute;i gọn trải nghiệm giải tr&iacute; v&agrave;o m&agrave;n h&igrave;nh 6.4 inch 1\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-2.jpg\" alt=\"G&oacute;i gọn trải nghiệm giải tr&iacute; v&agrave;o m&agrave;n h&igrave;nh 6.4 inch \" /></p>\r\n<h3><strong>Vi&ecirc;n pin lớn ẩn m&igrave;nh trong khung vỏ mỏng gọn</strong></h3>\r\n<p>Samsung Galaxy A31 sở hữu thiết kế mỏng 8.6mm cực k&igrave; thời trang, nhưng kh&ocirc;ng v&igrave; thế m&agrave; m&aacute;y c&oacute; dung lượng pin thấp. Tr&aacute;i lại, Galaxy A31 mang trong m&igrave;nh vi&ecirc;n pin lớn tới 5.000 mAh, c&oacute; thể đảm bảo trải nghiệm thoải m&aacute;i suốt ng&agrave;y d&agrave;i. Sản phẩm sẽ khiến bạn h&agrave;i l&ograve;ng khi cầm nắm v&agrave; thao t&aacute;c nhờ bộ khung vỏ được tạo d&aacute;ng kh&eacute;o l&eacute;o vừa vặn với l&ograve;ng b&agrave;n tay. Mặt lưng m&aacute;y c&oacute; hoạ tiết tạo khối h&igrave;nh học cắt xẻ độc đ&aacute;o v&agrave; được t&ocirc; điểm bởi những sắc m&agrave;u thời trang như xanh, đen v&agrave; trắng.</p>\r\n<p><img id=\"Vi&ecirc;n pin lớn ẩn m&igrave;nh trong khung vỏ mỏng gọn 1\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-3.jpg\" alt=\"Vi&ecirc;n pin lớn ẩn m&igrave;nh trong khung vỏ mỏng gọn\" /></p>\r\n<h3><strong>Chụp h&igrave;nh sắc sảo mọi g&oacute;c độ với cụm bốn camera</strong></h3>\r\n<p>Cụm bốn camera ở ph&iacute;a sau Galaxy A31 được g&oacute;i gọn v&agrave;o trong module chữ nhật theo đ&uacute;ng xu hướng thiết kế chuẩn 2020. Trong đ&oacute;, camera ch&iacute;nh với độ ph&acirc;n giải 48MP sẽ đảm bảo h&igrave;nh ảnh chụp được lu&ocirc;n sắc n&eacute;t v&agrave; chi tiết, camera 8MP Ultra wide 123 độ mở ra g&oacute;c tr&ocirc;ng ảnh rộng r&atilde;i c&ograve;n camera Marco 5MP đem tới nhiều tinh chỉnh cần thiết khi chụp cận cảnh, đồng thời đảm bảo chủ thể khu&ocirc;n h&igrave;nh lu&ocirc;n nổi bật khi kết hợp với hiệu ứng Live Focus m&agrave; Depth camera 5MP mang lại.</p>\r\n<p><img id=\"Chụp h&igrave;nh sắc sảo mọi g&oacute;c độ với cụm bốn camera 1\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-4.JPG\" alt=\"Chụp h&igrave;nh sắc sảo mọi g&oacute;c độ với cụm bốn camera\" /></p>\r\n<h3><strong>Bắt trọn mọi quang cảnh trong tầm mắt</strong></h3>\r\n<p>Sự hiện diện của camera Ultra wide kết hợp với camera ch&iacute;nh 48MP đem tới hai t&ugrave;y chọn g&oacute;c tr&ocirc;ng ảnh l&agrave; 80 độ v&agrave; 123 độ. Khi cần chụp ảnh phong cảnh hoặc chụp ảnh nh&oacute;m, bạn h&atilde;y k&iacute;ch hoạt g&oacute;c chụp cực rộng để c&oacute; được những khu&ocirc;n h&igrave;nh to&agrave;n cảnh như &yacute; muốn.</p>\r\n<p><img id=\"Bắt trọn mọi quang cảnh trong tầm mắt 11\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-5.JPG\" alt=\"Bắt trọn mọi quang cảnh trong tầm mắt 1\" /></p>\r\n<h3><strong>Live Focus gi&uacute;p ảnh selfie th&ecirc;m cuốn h&uacute;t</strong></h3>\r\n<p>Để ph&aacute;t huy tối đa năng lực của camera 20MP tr&ecirc;n mặt trước Galaxy A31, nh&agrave; sản xuất đ&atilde; t&iacute;ch hợp t&iacute;nh năng Live Focus hỗ trợ người d&ugrave;ng l&agrave;m mờ bối cảnh khi chụp selfie. Mọi bức ảnh chụp từ camera trước của Samsung Galaxy A31 đều hết sức sắc n&eacute;t với chủ thể khu&ocirc;n h&igrave;nh nổi bật nhờ sự can thiệp của hiệu ứng x&oacute;a ph&ocirc;ng.</p>\r\n<p><img id=\"Live Focus gi&uacute;p ảnh selfie th&ecirc;m cuốn h&uacute;t 11\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-6.jpg\" alt=\"Live Focus gi&uacute;p ảnh selfie th&ecirc;m cuốn h&uacute;t 1\" /></p>\r\n<h3><strong>Hoạt động cả ng&agrave;y nhờ vi&ecirc;n pin lớn 5.000 mAh</strong></h3>\r\n<p>Khi m&agrave; smartphone trở th&agrave;nh c&ocirc;ng cụ li&ecirc;n lạc, l&agrave;m việc, học tập v&agrave; giải tr&iacute; cần thiết cho đời sống thường ng&agrave;y của ch&uacute;ng ta th&igrave; pin trở th&agrave;nh yếu tố quan trọng khi c&acirc;n nhắc lựa chọn một chiếc điện thoại. Hiểu được điều đ&oacute;, Samsung t&iacute;ch hợp cho Galaxy A31 vi&ecirc;n pin dung lượng lớn 5.000 mAh, đủ khả năng cung ứng năng lượng cho m&agrave;n h&igrave;nh 6.4 inch hoạt động xuy&ecirc;n suốt một ng&agrave;y d&agrave;i trải nghiệm. Khi hết pin bất chợt, c&ocirc;ng nghệ sạc nhanh 15W sẽ gi&uacute;p bạn nạp lại pin cho m&aacute;y rất nhanh v&agrave; hiệu quả.</p>\r\n<p><img id=\"Hoạt động cả ng&agrave;y nhờ vi&ecirc;n pin lớn 5.000 mAh 11\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-7.JPG\" alt=\"Hoạt động cả ng&agrave;y nhờ vi&ecirc;n pin lớn 5.000 mAh 1\" /></p>\r\n<h3><strong>Cấu h&igrave;nh mạnh mẽ v&agrave; hoạt động hiệu quả</strong></h3>\r\n<p>Chiếc điện thoại Samsung Galaxy A31 c&oacute; thể xử l&yacute; t&aacute;c vụ nhanh ch&oacute;ng nhờ chip xử l&yacute; Octa-core mạnh mẽ kết hợp với dung lượng RAM 6GB. Sản phẩm cho ph&eacute;p người d&ugrave;ng lưu trữ kho dữ liệu c&aacute; nh&acirc;n đồ sộ qua bộ nhớ trong 128GB. Nếu l&agrave; người c&oacute; th&oacute;i quen tải xuống phim ảnh v&agrave; file nhạc lưu sẵn trong m&aacute;y, bạn c&oacute; thể mở rộng kh&ocirc;ng gian lưu trữ qua thẻ nhớ ngo&agrave;i 512GB.</p>\r\n<p><img id=\"Cấu h&igrave;nh mạnh mẽ v&agrave; hoạt động hiệu quả 11\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-8.JPG\" alt=\"Cấu h&igrave;nh mạnh mẽ v&agrave; hoạt động hiệu quả 1\" /></p>\r\n<h3><strong>Tối ưu h&oacute;a hiệu suất với Game Booster</strong></h3>\r\n<p>Kh&ocirc;ng chỉ ph&aacute;t huy tối đa sức mạnh của thiết bị khi chơi game, c&ocirc;ng nghệ Game Booster c&ograve;n hạn chế th&ocirc;ng b&aacute;o l&agrave;m ảnh hưởng đến người d&ugrave;ng trong những cuộc giao tranh căng thẳng. Giao diện của Game Booster được thiết kế rất trực quan, sinh động, cho ph&eacute;p ch&uacute;ng ta theo d&otilde;i được nhiệt độ của m&aacute;y khi chơi game c&ugrave;ng c&aacute;c th&ocirc;ng số cần thiết như bộ nhớ v&agrave; pin.</p>\r\n<p><img id=\"Tối ưu h&oacute;a hiệu suất với Game Booster 11\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-9.JPG\" alt=\"Tối ưu h&oacute;a hiệu suất với Game Booster 1\" /></p>\r\n<h3><strong>Bảo vệ dữ liệu với cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh</strong></h3>\r\n<p>Bạn c&oacute; thể ho&agrave;n to&agrave;n an t&acirc;m khi sử dụng Samsung Galaxy A31 bởi chiếc điện thoại n&agrave;y được trang bị cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh hiện đại &ndash; đ&acirc;y l&agrave; c&ocirc;ng nghệ bảo mật sinh trắc học hiện đại bậc nhất tr&ecirc;n smartphone ng&agrave;y nay. Th&ocirc;ng qua thao t&aacute;c chạm đơn giản v&agrave;o m&agrave;n h&igrave;nh, bạn sẽ l&agrave; người duy nhất mở kh&oacute;a v&agrave; đăng nhập v&agrave;o b&ecirc;n trong thiết bị.</p>\r\n<p><img id=\"Bảo vệ dữ liệu với cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh 33\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/05/Mo-ta-san-pham-samsung-galaxy-a31-10.JPG\" alt=\"Bảo vệ dữ liệu với cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh 3\" /></p>', 1, 1, 1, 11, '2021-08-05 00:28:29', '2021-08-14 02:31:15', NULL),------------------------------------------

